import Vue from 'vue'
import VueRouter from 'vue-router'
import HomeView from './components/HomeView.vue'
import Login from './components/Login.vue'
import App from './App.vue'

Vue.use(VueRouter)

const routes = [
  { path: '/', component: HomeView },
  { path: '/login', component: Login }
]

const router = new VueRouter({
  routes
})

new Vue({
  router,
  render: h => h(App),
}).$mount('#app')


<template>
  <div class="main-page">
    <!-- 상단 레이아웃 -->
    <div class="top-layout">
      <div class="login-signup" @click="goToLogin">로그인 | 회원가입 | 고객센터</div>
    </div>

    <!-- 상단 밑 레이아웃 -->
    <div class="header">
      <div class="website-title">shopping</div>
      <div class="search-bar">
        <input type="text" placeholder="상품을 검색하세요">
      </div>
      <div class="cart">장바구니</div>
    </div>

    <!-- 기타 쇼핑몰 기능들 -->
    <!-- 예시로 몇 가지 기능을 추가하였습니다 -->
    <div class="features">
      <div class="feature-item">
        <div class="icon">아이콘</div>
        <div class="text">기능 1</div>
      </div>
      <div class="feature-item">
        <div class="icon">아이콘</div>
        <div class="text">기능 2</div>
      </div>
      <div class="feature-item">
        <div class="icon">아이콘</div>
        <div class="text">기능 3</div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  methods: {
    goToLogin() {
  // 로그인 페이지로 이동
  this.$router.push('/login');
     }

    }
  }
}
</script>


<style>
.main-page {
  font-family: Arial, sans-serif;
  margin: 0 auto;
  max-width: 1200px;
  padding: 20px;
}

.top-layout {
  display: flex;
  justify-content: flex-end;
}

.login-signup {
  font-size: 14px;
  color: #888;
}

.header {
  display: flex;
  align-items: center;
  margin-top: 20px;
}

.website-title {
  font-size: 24px;
  font-weight: bold;
  margin-right: 20px;
}

.search-bar input {
  width: 300px;
  padding: 8px;
  border: 1px solid #ccc;
  border-radius: 4px;
  outline: none;
}

.cart {
  margin-left: auto;
  font-size: 14px;
  color: #888;
}

.features {
  display: flex;
  margin-top: 40px;
}

.feature-item {
  flex-basis: 33.33%;
  text-align: center;
}

.icon {
  font-size: 40px;
  margin-bottom: 10px;
}

.text {
  font-size: 14px;
}
</style>
