        quantity: 2
      }]
    };
  },

  methods: {
    increaseQuantity(item) {
      item.quantity++;
    },

    decreaseQuantity(item) {
      if (item.quantity > 1) {
        item.quantity--;
      }
    },

    removeFromCart(item) {
      const index = this.cartItems.findIndex(cartItem => cartItem.id === item.id);

      if (index !== -1) {
        this.cartItems.splice(index, 1);
      }
    }

  }
};
